type AtomicCSSObject = {
  [key in keyof CSSObject]: CSSObject[key] | ThemeAccessor;
} &
  {
    [key in keyof Partial<Aliases>]: CSSObject[key] | ThemeAccessor;
  };

const aliases: { [key in keyof CSSObject]: keyof Aliases } = {
  padding: 'p',
  paddingLeft: 'pl',
  paddingRight: 'pr',
  paddingTop: 'pt',
  paddingBottom: 'pb',
  margin: 'm',
  marginLeft: 'ml',
