export const padding = <P extends PaddingProps>(props: P) => {
  const padding = toPx(getProperty(props, 'padding'));
  const paddingLeft = toPx(getProperty(props, 'paddingLeft'));
  const paddingRight = toPx(getProperty(props, 'paddingRight'));
  const paddingTop = toPx(getProperty(props, 'paddingTop'));
  const paddingBottom = toPx(getProperty(props, 'paddingBottom'));
  return interpolate({
    padding,
    paddingLeft,
    paddingRight,
    paddingTop,
    paddingBottom,
  });
};

